text:
{{get_evaluations.data.filter(
    item => item.userId == Select_cubee.selectedOptionValue &&
            item.writer == item.userId &&
            item.competenceId == currentItem._id &&
            new Date(item.date) > new Date(new Date().getTime() - (3 * 7 * 24 * 60 * 60 * 1000))
  ).length > 0 ? "   Ondernomen acties en zelfevlauatie "+Select_cubee.selectedOptionLabel+ ": \n\n" + currentItem.feedback :"Heeft nog geen zelfevaluatie gedaan! \nLaat "+Select_cubee.selectedOptionLabel+ " eerst een zelfevaluatie doen."}}

textColor:
{{get_evaluations.data.filter(
    item => item.userId == Select_cubee.selectedOptionValue &&
            item.writer == item.userId &&
            item.competenceId == currentItem._id &&
            new Date(item.date) > new Date(new Date().getTime() - (3 * 7 * 24 * 60 * 60 * 1000))
  ).length > 0 ? "black" : "red"}}
